mapper.select:

select 

from jin_ledger
where 1=1

    and ledgerid = #{ledgerid,jdbcType=VARCHAR}


    and tuzhibianhao = concat('%',#{tuzhibianhao,jdbcType=VARCHAR},'%')


    and tuzhimingcheng like concat('%',#{tuzhimingcheng,jdbcType=VARCHAR},'%')


   and xingzhuang = #{xingzhuang,jdbcType=TINYINT}


    and cailiao = #{cailiao,jdbcType=TINYINT}


   and createTime > #{createTime,jdbcType=TIMESTAMP}



   and createTime < #{createTime_end,jdbcType=TIMESTAMP}
